select

FROM xz_company c LEFT JOIN xz_company_product p on c.company_id = p.company_id LEFT JOIN xz_company_image i on c.company_id = i.company_id
where c.company_id = #{companyId,jdbcType=BIGINT}

SELECT c.company_id,count(p.postion_id) as pcount from xz_company c LEFT JOIN xz_company_domain d ON c.company_id=d.company_id LEFT JOIN xz_field f ON f.field_id=d.field_id LEFT JOIN xz_postion p on c.company_id = p.company_id WHERE delete_falt = 0
AND f.field_name in
#{field.fieldName,jdbcType=VARCHAR}
and c.company_city = #{companyCity,jdbcType=VARCHAR}
and c.company_name like CONCAT('%',#{companyName,jdbcType=VARCHAR},'%')
GROUP BY c.company_id

SELECT company_name,company_id FROM  xz_company

  WHERE company_id in

    #{company_id,jdbcType=BIGINT}

select DISTINCT c.company_id,ppc.pcount,w.welfare_name,c.company_picture from xz_company c LEFT JOIN (select c.company_id,COUNT(p.postion_id) pcount from xz_company c LEFT JOIN xz_postion p on c.company_id = p.company_id GROUP BY c.company_id) ppc on c.company_id = ppc.company_id LEFT JOIN xz_company_domain d ON c.company_id=d.company_id LEFT JOIN xz_field f ON f.field_id=d.field_id LEFT JOIN xz_company_welfare w ON c.company_id=w.company_id WHERE delete_falt=0
and c.company_city=#{companyCity,jdbcType=BIGINT}
and c.company_name like CONCAT('%',#{companyName,jdbcType=VARCHAR},'%')
AND f.field_name in
#{field_name,jdbcType=VARCHAR}
and c.company_state=2 GROUP BY c.company_id
