mapper.select:
SELECT p.postion_id,p.company_id,p.postion_name ,p.postion_time,p.publish_time,p.over_time,p.postion_welfare,p.delete_time,p.postion_company, count(CASE ps.send_state WHEN '0' THEN 1 WHEN '1' THEN 1 WHEN '2' THEN 1 ELSE null END) alls, count(CASE ps.send_state WHEN '0' THEN 1 ELSE null END ) wcl, count(CASE ps.send_state WHEN '1' THEN 1 ELSE null END ) yxgt, count(CASE ps.send_state WHEN '2' THEN 1 ELSE null END ) ms, count(CASE ps.send_type WHEN '1' THEN ps.send_type ELSE null END ) zdxz FROM xz_postion p LEFT JOIN xz_postion_send ps on p.postion_id = ps.postion_id where p.company_id = #{companyId,jdbcType=BIGINT}
and postion_name like CONCAT('%',#{postionName},'%')
and postion_space like CONCAT('%',#{postionSpace},'%')
and p.postion_welfare = #{postionWelfare,jdbcType=VARCHAR}
and p.publish_time BETWEEN #{beginDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}
and p.publish_time > #{beginDate,jdbcType=TIMESTAMP}
and p.publish_time < #{endDate,jdbcType=TIMESTAMP}
GROUP BY p.postion_id

select DISTINCT
p.postion_id
from xz_postion p LEFT JOIN xz_company c on p.company_id = c.company_id LEFT JOIN xz_company_domain d on c.company_id = d.company_id LEFT JOIN xz_field f on d.field_id = f.field_id
where p.delete_flat = 0 and (f.field_type = 1 or f.field_type is null)

  and p.postion_space like CONCAT('%',#{workspace,jdbcType=VARCHAR},'%')


  and p.postion_name like CONCAT('%',#{likeStr,jdbcType=VARCHAR},'%')


  and f.field_name in

    #{field}



  and p.postion_mm >= #{salaryMin,jdbcType=INTEGER}


  and p.postion_ym <= #{salaryMax,jdbcType=INTEGER}


  and c.company_nature = #{companyNature,jdbcType=INTEGER}



    and p.postion_time >= date_sub(curdate(),interval 1 MONTH)


    and p.postion_time >= date_sub(curdate(),interval #{fadate,jdbcType=INTEGER} day)



  and c.company_scale = #{companyScale,jdbcType=VARCHAR}

GROUP BY p.postion_id

select 

from xz_postion p
where p.postion_id = #{postionId,jdbcType=BIGINT}

SELECT

FROM xz_postion p WHERE delete_flat = 0

AND  postion_space = #{postionSpace,jdbcType=VARCHAR}


AND  postion_name like CONCAT('%',#{postionName,jdbcType=VARCHAR},'%')


AND  company_id = #{companyId,jdbcType=BIGINT}

select
FROM xz_postion p LEFT JOIN xz_company_welfare w ON p.company_id = w.company_id LEFT JOIN xz_company c ON p.company_id = c.company_id LEFT JOIN xz_company_domain d on c.company_id = d.company_id LEFT JOIN xz_field f on d.field_id = f.field_id WHERE p.postion_name LIKE CONCAT('%',#{postionName,jdbcType=VARCHAR},'%')
and p.postion_id <>#{postionId,jdbcType=BIGINT}
